#include <stdio.h> #include <cs50.h> bool check_card ( long card ); int get_length ( long card ); bool checksum ( long card ); void print_card_type ( long card ); int main ( void ) { long card_number ; // Prompt user for credit card number do { card_number = get_long ( "Number: " ); } while ( card_number < 0 ); // Print card type if ( check_card ( card_number ) == true ) { print_card_type ( card_number ); } else { printf ( "INVALID 
 " ); } } // Check card lenth and check sum bool check_card ( long card ) { int length = get_length ( card ); return ( length == 13 || length == 15 || length == 16 ) && checksum ( card ); } // Get the length of credit card int get_length ( long card ) { int i ; for ( i = 0 ; card != 0 ; card /= 10 , i ++ ); return i ; } // Check sum bool checksum ( long card ) { int sum = 0 ; int i ; for ( i = 0 ; card != 0 ; card /= 10 , i ++ ) { if ( i % 2 == 0 ) { sum += card % 10 ; } else { int multiple = 2 * ( card % 10 ); sum += multiple / 10 + multiple % 10 ; } } return ( sum % 10 ) == 0 ; } // Identify and print card type void print_card_type ( long card ) { if (( card >= 34e13 && card < 35e13 ) || ( card >= 37e13 && card < 38e13 )) { printf ( "AMEX 
 " ); } else if ( card >= 51e14 && card < 56e14 ) { printf ( "MASTERCARD 
 " ); } else if (( card >= 4e12 && card < 5e12 ) || ( card >= 4e15 && card < 5e15 )) { printf ( "VISA 
 " ); } else { printf ( "INVALID 
 " ); } }

#include <cs50.h> #include <stdio.h> #include <string.h> // Max number of candidates #define MAX 9 // Candidates have name and vote count typedef struct { string name ; int votes ; } candidate ; // Array of candidates candidate candidates [ MAX ]; // Number of candidates int candidate_count ; // Function prototypes bool vote ( string name ); void print_winner ( void ); int main ( int argc , string argv []) { // Check for invalid usage if ( argc < 2 ) { printf ( "Usage: plurality [candidate ...] 
 " ); return 1 ; } // Populate array of candidates candidate_count = argc - 1 ; if ( candidate_count > MAX ) { printf ( "Maximum number of candidates is %i 
 " , MAX ); return 2 ; } for ( int i = 0 ; i < candidate_count ; i ++ ) { candidates [ i ]. name = argv [ i + 1 ]; candidates [ i ]. votes = 0 ; } int voter_count = get_int ( "Number of voters: " ); // Loop over all voters for ( int i = 0 ; i < voter_count ; i ++ ) { string name = get_string ( "Vote: " ); // Check for invalid vote if ( ! vote ( name )) { printf ( "Invalid vote. 
 " ); } } // Display winner of election print_winner (); } // Update vote totals given a new vote bool vote ( string name ) { int b = 0 ; for ( int i = 0 ; i < candidate_count ; i ++ ) { if ( strcmp ( candidates [ i ]. name , name ) == 0 ) { candidates [ i ]. votes = candidates [ i ]. votes + 1 ; b = b + 1 ; return true ; } } return b != 0 ; } // Print the winner (or winners) of the election void print_winner ( void ) { int max = candidates [ 0 ]. votes ; for ( int i = 1 ; i < candidate_count ; i ++ ) { if ( candidates [ i ]. votes > max ) { max = candidates [ i ]. votes ; } } for ( int i = 0 ; i < candidate_count ; i ++ ) { if ( candidates [ i ]. votes == max ) { printf ( "%s 
 " , candidates [ i ]. name ); } } }

// Modifies the volume of an audio file #include <stdint.h> #include <stdio.h> #include <stdlib.h> // Number of bytes in .wav header const int HEADER_SIZE = 44 ; // Create an array to store header file uint8_t header [ HEADER_SIZE ]; // using the int16_t type to store audio sample int16_t buffer ; int main ( int argc , char * argv []) { // Check command-line arguments if ( argc != 4 ) { printf ( "Usage: ./volume input.wav output.wav factor 
 " ); return 1 ; } // Open files and determine scaling factor FILE * input = fopen ( argv [ 1 ], "r" ); if ( input == NULL ) { printf ( "Could not open file. 
 " ); return 1 ; } FILE * output = fopen ( argv [ 2 ], "w" ); if ( output == NULL ) { printf ( "Could not open file. 
 " ); return 1 ; } float factor = atof ( argv [ 3 ]); // TODO: Copy header from input file to output file fread ( & header , sizeof ( header ), 1 , input ); fwrite ( & header , sizeof ( header ), 1 , output ); // TODO: Read samples from input file and write updated data to output file while ( fread ( & buffer , sizeof ( buffer ), 1 , input )) { buffer *= factor ; fwrite ( & buffer , sizeof ( buffer ), 1 , output ); } // Close files fclose ( input ); fclose ( output ); }

